      let filePath="";
      for await (const result of Ipfs.add(encrypt)) {
        await state.contract.methods
          .set(result.path)
          .send({from: state.accounts[0]});
        console.log("final-2");
        filePath = result.path;
        dispatch({type: 'SET_PATH', payload: result.path});
        dispatch({
          type: 'SET_NOTIF',
          payload: {msg: result.path, success: true}
