import React, { useState,useEffect } from 'react';
import {Link, useNavigate} from 'react-router-dom'
import  Axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
function LoginPage() {
const navigate=useNavigate();
useEffect(() => {
      // console.log(sessionStorage.length)
      if(sessionStorage.length>0)
      {
        navigate('/main')
      }
      },[]);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  function handleUsernameChange(event) {
    setUsername(event.target.value);
  }

  function handlePasswordChange(event) {
    setPassword(event.target.value);
  }

  function handleSubmit(event) {
    alert("gvcduguv")
    event.preventDefault();
    console.log(username);
    console.log(password);
       Axios.post("http://localhost:3001/login",{
        username:username,
        password:password,
       })
       .then((res)=>{
        console.log("fdigjhifj");
        if(Object.values(res.data).length===2)
        {
          const {vals}=res.data.result[0]
          alert('Login Successful')
          // console.log(res.data.result[0].firstname)
          
          sessionStorage.setItem('username',res.data.result[0].username)
          sessionStorage.setItem('loggined',true)
          navigate('/main')
        }
        else{
          alert(res.message)
          navigate('/')
        }
       })
    // TODO: Handle login logic with username and password
  }

  return (
    <div className="container-fluid vh-100 d-flex justify-content-center align-items-center login-bg">
      <div className="card login-card">
        <div className="card-body">
          <h1 className="card-title text-center">Login</h1>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="username" className="mb-2">Username:</label>
              <input type="text" value={username} id="username" name="username" className="form-control form-control-lg rounded-0" onChange={handleUsernameChange} />
            </div>
            <div className="form-group">
              <label htmlFor="password" className="mb-2">Password:</label>
              <input type="password" id="password" name="password" value={password} className="form-control form-control-lg rounded-0" onChange={handlePasswordChange} />
            </div>
            <button type="submit" className="btn btn-primary btn-block rounded-0">Login</button>
          </form>
          <div className="mt-3 text-center">
            <Link to="/register">Register</Link>
          </div>
        </div>
      </div>
    </div>
  );
}

export default LoginPage;
