import React, { useState,useEffect } from 'react';
import {Link, useNavigate} from 'react-router-dom'
import  Axios from 'axios';

function LoginPage() {
const navigate=useNavigate();
useEffect(() => {
      // console.log(sessionStorage.length)
      if(sessionStorage.length>0)
      {
        navigate('/main')
      }
      },[]);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  function handleUsernameChange(event) {
    setUsername(event.target.value);
  }

  function handlePasswordChange(event) {
    setPassword(event.target.value);
  }

  function handleSubmit(event) {
    alert("gvcduguv")
    event.preventDefault();
    console.log(username);
    console.log(password);
       Axios.post("http://localhost:3001/login",{
        username:username,
        password:password,
       })
       .then((res)=>{
        console.log("fdigjhifj");
        if(Object.values(res.data).length===2)
        {
          const {vals}=res.data.result[0]
          alert('Login Successful')
          // console.log(res.data.result[0].firstname)
          
          sessionStorage.setItem('username',res.data.result[0].username)
          sessionStorage.setItem('loggined',true)
          navigate('/main')
        }
        else{
          alert(res.message)
          navigate('/')
        }
       })
    // TODO: Handle login logic with username and password
  }

  return (
    <div className="container py-5">
      <div className="row justify-content-center">
        <div className="col-md-6 col-lg-5">
          <div className="card shadow-lg">
            <div className="card-body p-5">
              <h1 className="card-title text-center mb-5">Login</h1>
              <form onSubmit={handleSubmit}>
                <div className="form-group mb-4">
                  <label htmlFor="username" className="form-label">Username:</label>
                  <input type="text" value={username} id="username" name="username" className="form-control" onChange={handleUsernameChange} />
                </div>
                <div className="form-group mb-4">
                  <label htmlFor="password" className="form-label">Password:</label>
                  <input type="password" id="password" name="password" value={password} className="form-control" onChange={handlePasswordChange} />
                </div>
                <div className="d-grid gap-2">
                  <button type="submit" className="btn btn-primary btn-lg">Login</button>
                </div>
              </form>
              <div className="text-center mt-4">
                <Link to="/register">Register</Link>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default LoginPage;
